\t\t\t\t{","\t\t\t\t\t\t\techo \"You have already entered absence for \".$date.\". Please cancel your absence for the given date first and try again.\";","\t\t\t\t\t\t\texit;","\t\t\t\t\t\t}","\t\t\t\t\t\tif($b == 1 || $l == 1 || $s == 1 || $d == 1)","\t\t\t\t\t\t{","\t\t\t\t\t\t\t$query = \"insert into ABSENTEES(ID,DATE,BREAKFAST,LUNCH,SNACKS,DINNER) values('$id','$date','$b','$l','$s','$d')\";","\t\t\t\t\t\t\t$result = mysql_query($query);","\t\t\t\t\t\t\tif(!empty($result))","\t\t\t\t\t\t\t{","\t\t\t\t\t\t\t\techo \"Your upcoming absence for \".$date.\" has been saved.\";","\t\t\t\t\t\t\t}","\t\t\t\t\t\t}","\t\t\t\t\t\telse {","\t\t\t\t\t\t\techo \"Please select atleast one meal you wish to be absent for.\";","\t\t\t\t\t\t}","\t\t\t\t\t}","\t\t\t\t\tif(isset($_POST['cancel_absence']))","\t\t\t\t\t{","\t\t\t\t\t\t$date = $_POST['date'];","\t\t\t\t\t\t$q1 = \"select * from ABSENTEES where ID = '$id' and DATE = '$date'\";","\t\t\t\t\t\t$result1 = mysql_query($q1);","\t\t\t\t\t\tif(mysql_num_rows($result1) > 0)\t\t\t\t\t\t\t//check if entry with same ID & DATE exists (which must satisfy)","\t\t\t\t\t\t{","\t\t\t\t\t\t\t$query = \"delete from ABSENTEES where ID = '$id' and DATE = '$date'\";","\t\t\t\t\t\t\tmysql_query($query);","\t\t\t\t\t\t\tif(mysql_affected_rows() > 0)","\t\t\t\t\t\t\t{","\t\t\t\t\t\t\t\techo \"Your absence for \".$date.\" has been cancelled.\";","\t\t\t\t\t\t\t}","\t\t\t\t\t\t}","\t\t\t\t\t\telse {","\t\t\t\t\t\t\techo \"You have not entered absence for \".$date.\" yet.\";","\t\t\t\t\t\t}","\t\t\t\t\t}","\t\t\t\t?>","\t\t\t\t</center>","\t\t\t</div>","\t\t</div>","\t\t","\t\t<div id=\"sidebar\">","\t\t\t<div class=\"box1\">","\t\t\t\t<div class=\"title\">","\t\t\t\t\t<center><h2>Announcement</h2></center>","\t\t\t\t</div>","\t\t\t\t<ul>","\t\t\t\t\t<marquee style=\"height: 410px;\" behavior=\"scroll\" direction=\"up\" scrollamount=\"2\" onMouseOver=\"this.stop()\" onMouseOut=\"this.start()\">","\t\t\t\t\t\t<li>","\t\t\t\t\t\t\t<?php","\t\t\t\t\t\t